/**
 * Turn an mdast syntax tree into markdown.
 *
 * @param {Nodes} tree
 *   Tree to serialize.
 * @param {Options} [options]
 *   Configuration (optional).
 * @returns {string}
 *   Serialized markdown representing `tree`.
 */
export function toMarkdown(tree: Nodes, options?: import("mdast-util-to-markdown/lib/types.js").Options | undefined): string;
export type Nodes = import('mdast').Nodes;
export type Enter = import('mdast-util-to-markdown/lib/types.js').Enter;
export type Info = import('mdast-util-to-markdown/lib/types.js').Info;
export type Join = import('mdast-util-to-markdown/lib/types.js').Join;
export type FlowParents = import('mdast-util-to-markdown/lib/types.js').FlowParents;
export type Options = import('mdast-util-to-markdown/lib/types.js').Options;
export type PhrasingParents = import('mdast-util-to-markdown/lib/types.js').PhrasingParents;
export type SafeConfig = import('mdast-util-to-markdown/lib/types.js').SafeConfig;
export type State = import('mdast-util-to-markdown/lib/types.js').State;
export type TrackFields = import('mdast-util-to-markdown/lib/types.js').TrackFields;
